c_break_continue.syntax:
int i;for (i = 0; i < 10; i++) {  if (i == 4) {    
break;  }  printf("%d\n", i);}

int i;for (i = 0; i < 10; i++) {  if (i == 4) {    
continue;  }  printf("%d\n", i);} 

int i = 0;while (i < 10) {  if (i == 4) {    
break;  }  printf("%d\n", i);  i++;}

