c_user_input.syntax:
  // Create an integer variable that will store the number we get from the userint myNum;// Ask the user to type a numberprintf("Type a number: 
  \n");// Get and save the number the user typesscanf("%d", &myNum);
// Output the number the user typedprintf("Your number is: %d", myNum);

  // Create an int and a char variableint myNum;char myChar;
  // Ask the user to type a number AND a characterprintf("Type a number AND 
  a character and press enter: \n"); // Get and save the number AND 
  character the user typesscanf("%d %c", &myNum, &myChar);// Print 
  the numberprintf("Your number is: %d\n", myNum);// Print the 
  characterprintf("Your character is: %c\n", myChar);

  // Create a stringchar firstName[30];// Ask the user to input some 
  textprintf("Enter your first name: \n");// Get and save the text
  scanf("%s", firstName);// Output the textprintf("Hello %s", 
  firstName);

